import pandas as pd
import argparse
from fmh_omega.helperfuncs import positive_selection_based_on_mutation_rate_p, negative_selection_based_on_mutation_rate_p
import random
from Bio.Seq import Seq
import gzip

#input file info
WD='/data/jzr5814/sourmash_dnds_estimation/tests/test/real_sequence_selection_mutation_0.001'
#mutation rate p for mutation purposes
mutation_rate_p = 0.001
#get sequences from fasta file
ref_sequence = ''
name = 'K06240'
# Create 100 random mutated sequences
ITERATIONS = 100

#output files
POSITIVE_SELECTION_QUERIES_NT = open(f'{WD}/positive_selection_queries_{mutation_rate_p}_{name}.fna','w')
POSITIVE_SELECTION_QUERIES_NT.write(f'>positive_{mutation_rate_p}_ref\n')
POSITIVE_SELECTION_QUERIES_NT.write(f'{ref_sequence}\n')

POSITIVE_SELECTION_QUERIES_PROTEIN = open(f'{WD}/positive_selection_translated_queries_{mutation_rate_p}_{name}.faa','w')
POSITIVE_SELECTION_QUERIES_PROTEIN.write(f'>positive_{mutation_rate_p}_ref\n')
translated_positive_queries = Seq(ref_sequence).translate()
POSITIVE_SELECTION_QUERIES_PROTEIN.write(f'{translated_positive_queries}\n')

NEGATIVE_SELECTION_QUERIES_NT = open(f'{WD}/negative_selection_queries_{mutation_rate_p}_{name}.fna','w')
NEGATIVE_SELECTION_QUERIES_NT.write(f'>negative_{mutation_rate_p}_ref\n')
NEGATIVE_SELECTION_QUERIES_NT.write(f'{ref_sequence}\n')

NEGATIVE_SELECTION_QUERIES_PROTEIN = open(f'{WD}/negative_selection_translated_queries_{mutation_rate_p}_{name}.faa','w')
NEGATIVE_SELECTION_QUERIES_PROTEIN.write(f'>negative_{mutation_rate_p}_ref\n')
query_negative_nt_seq = Seq(ref_sequence).translate()
NEGATIVE_SELECTION_QUERIES_PROTEIN.write(f'{query_negative_nt_seq}\n')

for i in range(ITERATIONS):
    #ref sequence is mutated with mutation rate p
    query_positive_nt_seq = positive_selection_based_on_mutation_rate_p(ref_sequence,float(mutation_rate_p))
    POSITIVE_SELECTION_QUERIES_NT.write(f'>positive_{mutation_rate_p}_{i}\n')
    POSITIVE_SELECTION_QUERIES_NT.write(f'{query_positive_nt_seq}\n')

    #Translated mutated sequences 
    translated_positive_queries = Seq(query_positive_nt_seq).translate()
    POSITIVE_SELECTION_QUERIES_PROTEIN.write(f'>positive_{mutation_rate_p}_{i}\n')
    POSITIVE_SELECTION_QUERIES_PROTEIN.write(f'{translated_positive_queries}\n')

    #ref sequence is mutated with mutation rate p
    query_negative_nt_seq = negative_selection_based_on_mutation_rate_p(ref_sequence,float(mutation_rate_p))
    NEGATIVE_SELECTION_QUERIES_NT.write(f'>negative_{mutation_rate_p}_{i}\n')
    NEGATIVE_SELECTION_QUERIES_NT.write(f'{query_negative_nt_seq}\n')

    #Translated mutated sequences 
    translated_negative_queries = Seq(query_negative_nt_seq).translate()
    NEGATIVE_SELECTION_QUERIES_PROTEIN.write(f'>negative_{mutation_rate_p}_{i}\n')
    NEGATIVE_SELECTION_QUERIES_PROTEIN.write(f'{translated_negative_queries}\n')

